INSERT INTO `wp_snippets` (`id`, `name`, `description`, `code`, `tags`, `scope`, `priority`, `active`) VALUES
(1, 'Example HTML shortcode', 'This is an example snippet for demonstrating how to add an HTML shortcode. You can remove it, or edit it to add your own content.', '\nadd_shortcode( \'shortcode_name\', function () {\n\n	$out = \'<p>write your HTML shortcode content here</p>\';\n\n	return $out;\n} );', 'shortcode', 'global', 10, 0),
(2, 'Example CSS snippet', 'This is an example snippet for demonstrating how to add custom CSS code to your website. You can remove it, or edit it to add your own content.', '\nadd_action( \'wp_head\', function () { ?>\n	<style>\n\n		/* write your CSS code here */\n\n	</style>\n<?php } );\n', 'css', 'front-end', 10, 0),
(3, 'Example JavaScript snippet', 'This is an example snippet for demonstrating how to add custom JavaScript code to your website. You can remove it, or edit it to add your own content.', '\nadd_action( \'wp_head\', function () { ?>\n	<script>\n\n		/* write your JavaScript code here */\n\n	</script>\n<?php } );\n', 'javascript', 'front-end', 10, 0),
(4, 'Order snippets by name', 'Order snippets by name by default in the snippets table. You can remove it, or edit it to add your own content.', '\nadd_filter( \'code_snippets/list_table/default_orderby\', function () {\n	return \'name\';\n} );\n', 'code-snippets-plugin', 'admin', 10, 0),
(5, 'Secondary Menu', '', 'add_filter( \'generate_woocommerce_menu_item_location\', \'tu_move_menu_cart_item\' );\r\nfunction tu_move_menu_cart_item() {\r\n    return \'secondary\';\r\n}', '', 'global', 10, 0),
(6, 'Registration Redirect', '', 'add_filter( \'woocommerce_registration_redirect\', \'custom_redirection_after_registration\', 10, 1 );\r\nfunction custom_redirection_after_registration( $redirection_url ){\r\n    // Change the redirection Url\r\n   $redirection_url = get_permalink( wc_get_page_id( \'shop\' ) ); // Store page\r\n\r\n    return $redirection_url; // Always return something\r\n}', '', 'global', 10, 1),
(7, 'Login Redirect', '', '\r\n/**\r\n * Redirect users to custom URL based on their role after login\r\n *\r\n * @param string $redirect\r\n * @param object $user\r\n * @return string\r\n */\r\nfunction wc_custom_user_redirect( $redirect, $user ) {\r\n	// Get the first of all the roles assigned to the user\r\n	$role = $user->roles[0];\r\n	$dashboard = admin_url();\r\n	$shop = get_permalink( wc_get_page_id( \'shop\' ) );\r\n	if( $role == \'administrator\' ) {\r\n		//Redirect administrators to the dashboard\r\n		$redirect = $dashboard;\r\n	} elseif ( $role == \'shop-manager\' ) {\r\n		//Redirect shop managers to the dashboard\r\n		$redirect = $dashboard;\r\n	} elseif ( $role == \'editor\' ) {\r\n		//Redirect editors to the dashboard\r\n		$redirect = $dashboard;\r\n	} elseif ( $role == \'author\' ) {\r\n		//Redirect authors to the dashboard\r\n		$redirect = $dashboard;\r\n	} elseif ( $role == \'customer\' || $role == \'subscriber\' ) {\r\n		//Redirect customers and subscribers to the \"My Account\" page\r\n		$redirect = $shop;\r\n	} else {\r\n		//Redirect any other role to the previous visited page or, if not available, to the home\r\n		$redirect = wp_get_referer() ? wp_get_referer() : home_url();\r\n	}\r\n	return $redirect;\r\n}\r\nadd_filter( \'woocommerce_login_redirect\', \'wc_custom_user_redirect\', 10, 2 );', '', 'global', 10, 1),
(8, 'Quantity WooCommerce', '', '/**\r\n * Override loop template and show quantities next to add to cart buttons\r\n */\r\n\r\nadd_filter( \'woocommerce_loop_add_to_cart_link\', \'quantity_inputs_for_woocommerce_loop_add_to_cart_link\', 10, 2 );\r\nfunction quantity_inputs_for_woocommerce_loop_add_to_cart_link( $html, $product ) {\r\n	if ( $product && $product->is_type( \'simple\' ) && $product->is_purchasable() && $product->is_in_stock() && ! $product->is_sold_individually() ) {\r\n		$html = \'<form action=\"\' . esc_url( $product->add_to_cart_url() ) . \'\" class=\"cart\" method=\"post\" enctype=\"multipart/form-data\">\';\r\n		$html .= woocommerce_quantity_input( array(), $product, false );\r\n		$html .= \'<button type=\"submit\" class=\"button alt\">\' . esc_html( $product->add_to_cart_text() ) . \'</button>\';\r\n		$html .= \'</form>\';\r\n	}\r\n	\r\n	return $html;\r\n}', '', 'global', 10, 1),
(9, 'Add Quantity ', '', '// For implementation instructions see: https://aceplugins.com/how-to-add-a-code-snippet/\r\n\r\n/**\r\n * Add quantity field on the shop page.\r\n */\r\nfunction ace_shop_page_add_quantity_field() {\r\n\r\n	/** @var WC_Product $product */\r\n	$product = wc_get_product( get_the_ID() );\r\n\r\n	if ( ! $product->is_sold_individually() && \'variable\' != $product->get_type() && $product->is_purchasable() ) {\r\n		woocommerce_quantity_input( array( \'min_value\' => 1, \'max_value\' => $product->backorders_allowed() ? \'\' : $product->get_stock_quantity() ) );\r\n	}\r\n\r\n}\r\nadd_action( \'woocommerce_after_shop_loop_item\', \'ace_shop_page_add_quantity_field\', 12 );\r\n\r\n\r\n/**\r\n * Add required JavaScript.\r\n */\r\nfunction ace_shop_page_quantity_add_to_cart_handler() {\r\n\r\n	wc_enqueue_js( \'\r\n		$(\".woocommerce .products\").on(\"click\", \".quantity input\", function() {\r\n			return false;\r\n		});\r\n		$(\".woocommerce .products\").on(\"change input\", \".quantity .qty\", function() {\r\n			var add_to_cart_button = $(this).parents( \".product\" ).find(\".add_to_cart_button\");\r\n\r\n			// For AJAX add-to-cart actions\r\n			add_to_cart_button.data(\"quantity\", $(this).val());\r\n\r\n			// For non-AJAX add-to-cart actions\r\n			add_to_cart_button.attr(\"href\", \"?add-to-cart=\" + add_to_cart_button.attr(\"data-product_id\") + \"&quantity=\" + $(this).val());\r\n		});\r\n\r\n		// Trigger on Enter press\r\n		$(\".woocommerce .products\").on(\"keypress\", \".quantity .qty\", function(e) {\r\n			if ((e.which||e.keyCode) === 13) {\r\n				$( this ).parents(\".product\").find(\".add_to_cart_button\").trigger(\"click\");\r\n			}\r\n		});\r\n	\' );\r\n\r\n}\r\nadd_action( \'init\', \'ace_shop_page_quantity_add_to_cart_handler\' );', '', 'global', 10, 0);
